import React from 'react'
import { ScrollToBottom } from 'react-scroll-to-bottom';

const ChatMessages = ({ messages, username }) => {
    return(
        <ScrollToBottom>
            {
                messages.map((message, index) => {

                        <div key={index}>
                            {message.text}
                        </div>

                })
            }
        </ScrollToBottom>
    )
}

export default ChatMessages;
